function App() {
  const [loading, setLoading] = useState(true);
  const { userLoginData } = useContext(authContext);
  
  // Move auth check inside useEffect to ensure context is ready
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    document.title = "AITS";
    // Check auth status after context is ready
    setIsAuthenticated(!!localStorage.getItem("token"));
    setLoading(false);
  }, [userLoginData]); // Add userLoginData as dependency

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  return (
    <>
      <Router>
        <Routes>
          {/* Always accessible routes */}
          <Route path="/" element={<WelcomePage />} />
          <Route path="/login" element={<LoginPage />} />
          
          {/* Protected routes */}
          {isAuthenticated ? (
            <>
              {/* Student Routes */}
              {userLoginData?.role === "student" && (
                <>
                  <Route path="/student" element={<StudentDashboard />} />
                  <Route path="/student/academic-issues" element={<AcademicIssuePage />} />
                </>
              )}

              {/* Lecturer Routes */}
              {userLoginData?.role === "lecturer" && (
                <>
                  <Route path="/lecturer" element={<LecturerDashboard />} />
                  <Route path="/lecturer/academic-issues" element={<AcademicIssuePage />} />
                </>
              )}

              {/* Registrar Routes */}
              {userLoginData?.role === "registrar" && (
                <Route path="/registrar-dashboard" element={<Registrardashboard />} />
              )}
            </>
          ) : null}

          {/* Fallback routes */}
          <Route 
            path="*" 
            element={
              isAuthenticated 
                ? <Navigate to={getDefaultRoute(userLoginData?.role)} replace /> 
                : <Navigate to="/login" replace />
            } 
          />
        </Routes>
      </Router>
      <ToastContainer {...toastProps} />
    </>
  );
}

// Helper function for default routes
function getDefaultRoute(role) {
  switch(role) {
    case 'student': return '/student';
    case 'lecturer': return '/lecturer';
    case 'registrar': return '/registrar-dashboard';
    default: return '/login';
  }
}
