import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./StudentDashboard.css";
import { FetchIssues } from "../services/issueService";
import Modal from "../components/Modal";
import Editpage from "../components/Editpage";
import toast from "react-hot-toast";
import LoadingSpinner from "../components/LoadingSpinner";

const StudentDashboard = ({ user = {} }) => {
  const navigate = useNavigate();
  const [courseUnits, setCourseUnits] = useState([]);
  const [singleIssue, setSingleIssue] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Fetch issues on component mount
  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsLoading(true);
        await FetchIssues(setCourseUnits);
      } catch (error) {
        toast.error("Failed to fetch issues");
        console.error("Fetch error:", error);
      } finally {
        setIsLoading(false);
      }
    };
    
    fetchData();
  }, []);

  const handleEdit = (course) => {
    setSingleIssue(course);
    openModal();
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    toast.success("Issues updated successfully!");
  };

  const openModal = () => setIsModalOpen(true);
  const closeModal = () => setIsModalOpen(false);

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  if (isLoading) {
    return <LoadingSpinner />;
  }

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <div>
          <h1>Student Dashboard</h1>
          <h2>Welcome, {user?.firstName || "Student"}! ðŸ‘‹</h2>
          <p>Track and update your issue resolutions</p>
        </div>
        <button onClick={handleLogout} className="logout-btn">
          Logout
        </button>
      </header>

      <main className="dashboard-content">
        <Modal isOpen={isModalOpen} onClose={closeModal}>
          <Editpage data={singleIssue} onClose={closeModal} />
        </Modal>

        <div className="issues-section">
          <form onSubmit={handleSubmit}>
            <div className="table-responsive">
              <table className="issues-table">
                <thead>
                  <tr>
                    <th>Course Unit</th>
                    <th>Status</th>
                    <th>Issue Details</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {courseUnits.length > 0 ? (
                    courseUnits.map((course, index) => (
                      <tr key={index} className={`status-${course.status.toLowerCase()}`}>
                        <td>{course.course_unit}</td>
                        <td>
                          <span className={`status-badge ${course.status.toLowerCase()}`}>
                            {course.status}
                          </span>
                        </td>
                        <td>{course.issue_details}</td>
                        <td>
                          <button
                            type="button"
                            className="edit-btn"
                            onClick={() => handleEdit(course)}
                          >
                            Edit
                          </button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="4" className="no-issues">
                        No issues found
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>

            <div className="actions-container">
              <button type="submit" className="update-btn">
                Update Issues
              </button>
              <button
                type="button"
                className="report-btn"
                onClick={() => navigate("/student/academic-issues")}
              >
                Report New Issue
              </button>
            </div>
          </form>
        </div>
      </main>
    </div>
  );
};

export default StudentDashboard;
